interface timeObject {
    years: number
    months: number
    days: number
    hours: number
    minutes: number
    seconds: number
    milliseconds: number
}

interface accurateTimeObject {
    centuries: number
    decades: number
    calendarYears: number
    months: number
    weeks: number
    days: number
    hours: number
    minutes: number
    seconds: number
    milliseconds: number
}

interface readableOptions {
    compact?: boolean
    showEmpty?: boolean
    showMS?: boolean
}

export { timeObject, accurateTimeObject, readableOptions }